-- CREATE OR REPLACE VIEW PRIDEPROT.PEP_PGROUP  AS
--  CREATE VIEW PRIDEPROT.PEP_PGROUP AS
--   SELECT DISTINCT PEP_UNIQ.PEPTIDE_ID,
--     PEP_UNIQ.UNIQUENESS,
--     PROT_PGRP.PROT_GROUP_ID
--   FROM
--     (SELECT PEP_GROUP.PEPTIDE_ID  AS PEPTIDE_ID,
--             COUNT(PEP_GROUP.PEPTIDE_ID) AS UNIQUENESS
--      FROM
--        (SELECT PEPTIDE.PEPTIDE_ID,
--           PROT_PGRP.PROT_GROUP_ID
--         FROM PEPTIDE,
--           PEP_PROT,
--           PROT_PGRP
--         WHERE PEPTIDE.PEPTIDE_ID = PEP_PROT.PEPTIDE_ID
--               AND PEP_PROT.PROTEIN_ID  = PROT_PGRP.PROTEIN_ID
--               AND PEPTIDE.SYMBOLIC     = 'TRUE'
--         GROUP BY PEPTIDE.PEPTIDE_ID,
--           PROT_PGRP.PROT_GROUP_ID
--        ) PEP_GROUP
--      GROUP BY PEP_GROUP.PEPTIDE_ID
--     ) PEP_UNIQ,
--     PEP_PROT,
--     PROT_PGRP
--   WHERE PEP_UNIQ.PEPTIDE_ID = PEP_PROT.PEPTIDE_ID
--         AND PEP_PROT.PROTEIN_ID   = PROT_PGRP.PROTEIN_ID
--   ORDER BY PEP_UNIQ.PEPTIDE_ID ;

-- CREATE OR REPLACE VIEW PRIDEPROT.PEP_PGROUP AS
CREATE VIEW PRIDEPROT.PEP_PGROUP AS
  SELECT
    DISTINCT PEP_UNIQ.PEPTIDE_ID,
    PEP_UNIQ.UNIQUENESS,
    PROT_PGRP.PROT_GROUP_ID
  FROM
    (SELECT
       PEP_GROUP.PEPTIDE_ID  AS PEPTIDE_ID,
       COUNT(PEP_GROUP.PEPTIDE_ID) AS UNIQUENESS
     FROM
       (SELECT
          PEPTIDE.PEPTIDE_ID,
          PROT_PGRP.PROT_GROUP_ID
        FROM PRIDEPROT.PEPTIDE,
          PRIDEPROT.PEP_PROT,
          PRIDEPROT.PROT_PGRP,
          PRIDEPROT.PROT_GROUP
        WHERE PEPTIDE.PEPTIDE_ID = PEP_PROT.PEPTIDE_ID
              AND PEP_PROT.PROTEIN_ID = PROT_PGRP.PROTEIN_ID
              AND PEPTIDE.SYMBOLIC = 'TRUE'
              AND PROT_GROUP.PROT_GROUP_ID = PROT_PGRP.PROT_GROUP_ID
              AND PROT_GROUP.PROT_GROUP_TYPE = 'ENTRY'
        GROUP BY PEPTIDE.PEPTIDE_ID,
       PROT_PGRP.PROT_GROUP_ID
    ) PEP_GROUP
  GROUP BY PEP_GROUP.PEPTIDE_ID
  ) PEP_UNIQ,
PRIDEPROT.PEP_PROT,
PRIDEPROT.PROT_PGRP,
  PRIDEPROT.PROT_GROUP
  WHERE PEP_UNIQ.PEPTIDE_ID = PEP_PROT.PEPTIDE_ID
AND PEP_PROT.PROTEIN_ID = PROT_PGRP.PROTEIN_ID
AND PROT_GROUP.PROT_GROUP_ID = PROT_PGRP.PROT_GROUP_ID
AND PROT_GROUP.PROT_GROUP_TYPE = 'ENTRY'
ORDER BY PEP_UNIQ.PEPTIDE_ID;


